execution.script:
node {
   stage('Preparation') {
     echo 'Deployment in a Dynatrace Deployment Event begins... '
   }
   stage('test') {
     steps{
         //wrap the deployment in a dynatrace deplyment event
         createDynatraceDeploymentEvent(
             envId: 'Dynatrace Tenant',
             tagMatchRules: [
                 [
                     meTypes: [[meType: 'SERVICE']],
                     tags: [
                         [context: 'CONTEXTLESS', key: '', value: "${env.APP_NAME}"]
                        ]
                    ]
                 ]){
                     // now we do the actual deployment
                     dir('./charts/carts') {
                         container('maven') {
                             sh 'jx step changelog --version v\$(cat ../../VERSION)'

                             //release the helm chart
                             sh 'jx step helm release'

                             //promite to staging
                             sh 'jx promote -b --env staging --timeout 1h --version \$(cat ../../VERSION) $APP_NAME'
                         }
                     }
                 }
            }
        }
   }
